function StepCounter(a,b){var c=this;c.$Container=a;c.CurrentStep=0;c.Period=0;c.Steps=b;c.Timer=null;this.setPeriod=function(a){c.Period=parseInt(a)};this.start=function(){c.CurrentStep=0;if(c.Period){c.incrementStep();c.Timer=setInterval(c.incrementStep,c.Period)}else{c.Timer=null}};this.incrementStep=function(){if(c.CurrentStep<c.Steps.length){c.$Container.html(c.Steps[c.CurrentStep]+"...");c.CurrentStep++}else{c.cancel()}};this.cancel=function(){if(c.Timer!=null){clearInterval(c.Timer);c.Timer=null}};this.clear=function(){this.cancel();c.$Container.html("")}}